import { Typography } from '@mui/material'
import { Box } from '@mui/system'
import React, { useEffect, useState } from 'react'
import ProductDetails from '../../Cart/List/ProductDetails/ProductDetails'
import DeleteIcon from '@mui/icons-material/Delete';
import IconButton from '@mui/material/IconButton';
import axios from 'axios';
import { baseUrl } from '../../../url'

function WishList() {

    const [products, setProducts] = useState([])
    useEffect(() => {
        let user = localStorage.getItem("user")
        user = JSON.parse(user)
        const customConfig = {
            headers: {
                'Authorization': `Bearer ${user.token}`
            }
        }

        axios.get(`${baseUrl}/api/wishlist/wishlistitems`, customConfig)
            .then((res) => {
                setProducts(res.data)
                console.log(res.data)
            }).catch((err) => {
                console.log(err);
            })
    }, [])

    return (
        <Box sx={{
            bgcolor: 'white',
            width: '65rem',
            p: '2rem',
            borderRadius: '1rem',
            boxShadow: "1px 1px 6px 0px grey"

        }}>
            <Typography><b>My WishList</b></Typography>
            <Box sx={{
                height: 350,
                overflow: "hidden",
                overflowY: "scroll",
            }}>
                {products && products.map((product) =>
                    <Box sx={{
                        border: 1,
                        borderColor: 'lightgrey',
                        width: '6rem',
                        display: 'flex',
                        justifyContent: 'space-between',
                        ml: '-2rem'
                    }}>
                        <Box sx={{
                            mt: '1rem',
                            mb: '1rem',
                            ml: '1rem'
                        }}>
                            <img src={baseUrl + '/' + product.url} alt='img' width={150} height={100}></img>
                        </Box>
                        <Box sx={{ ml: '5rem' }}>
                            {product && <ProductDetails item={product} />}
                        </Box>
                        <Box sx={{
                            ml: '3rem',
                            mt: '3rem'
                        }}>
                            <IconButton aria-label="delete">
                                <DeleteIcon />
                            </IconButton>
                        </Box>
                    </Box>
                )}
            </Box>
        </Box>
    )
}

export default WishList
