import * as React from 'react';
import Logo from './logo/logo';
import Cart from './cartIcon/cartIcon';
import Account from './accountIcon/accountIcon';
import SearchBar from './SearchBar/SearchBar'
import './header.css';
import Box from '@mui/material/Box';
import IconButton from '@mui/material/IconButton';
import { Typography } from '@mui/material';
import { useNavigate } from 'react-router-dom';


export default function PrimarySearchAppBar() {
    const [user, setUser] = React.useState({})
    const navigate = useNavigate()
    React.useEffect(() => {
        let user = localStorage.getItem("user");
        let User=JSON.parse
        setUser(user)
    }, [])

    return (
        <Box className='header' sx={{ borderBottom: 1 }}>
            <Box sx={{
                position: 'relative',
                width: '12rem',
                height: '4rem',
                display: { xs: 'none', sm: 'block' }
            }}>
                <Logo />
            </Box>
            <Box>
                <SearchBar />
            </Box>
            <Box className='right-items'>
                {user ? (
                    <Box>
                        <IconButton>
                            <Cart />
                        </IconButton>
                        <IconButton>
                            <Account />
                        </IconButton>
                    </Box>

                ) : (
                    <Box>
                        <Typography className='selection' onClick={() => navigate('/login')}>Login</Typography>
                        <Typography>/</Typography>
                        <Typography className='selection' onClick={() => navigate('/signup')}>Signup</Typography>
                    </Box>
                )}



            </Box>
        </Box>
    );
}
