import React from 'react'
import Slider from 'react-animated-slider';
import 'react-animated-slider/build/horizontal.css';
import './banner.css'

function banner() {
    const slides = [
        { title: 'The Land of Teast!', description: 'Best Quality Products', url: process.env.PUBLIC_URL + '/pickle1.png' },
        { title: 'Garlic pickle', description: 'NATURAL', url: process.env.PUBLIC_URL + '/pickle2.png' },
        { title: 'Organic Vegitables', description: 'GARLIC', url: process.env.PUBLIC_URL + '/pickle3.png' },
        { title: 'Fresh lemon', description: 'TEASTY', url: process.env.PUBLIC_URL + '/pickle4.png' },
    ];

    return (
        <div className='banner'>
            <Slider className='slider '
                autoplay={2000}
            >
                {slides.map((slide, index) =>
                    <div className='img-background'  key={index} >
                        <div className='img-text'>
                            <h2>{slide.title}</h2>
                            <div>{slide.description}</div>
                        </div>
                    </div>)
                }
            </Slider >
        </div >
    )
}

export default banner
