import { IconButton, Typography } from '@mui/material'
import { Box } from '@mui/system'
import React from 'react'
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';
import AddButton from './AddButton/Addaddress';
import axios from 'axios';
import { baseUrl } from '../../../url'


function Address() {
    const [address, setAddress] = React.useState([])
    React.useEffect(() => {
        let user = localStorage.getItem("user")
        user = JSON.parse(user)
        const customConfig = {
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${user.token}`
            }
        }
        axios.get(`${baseUrl}/api/user/address`, customConfig)
            .then((res) => {
                setAddress(res.data)
                console.log(res.data)
            })
    }, [])

    return (

        <Box
            sx={{
                bgcolor: 'white',
                width: '65rem',
                p: '2rem',
                borderRadius: '1rem',
                boxShadow: "1px 1px 6px 0px grey"

            }}>
            <Typography><b>Manage Address</b></Typography>
            <Box sx={{
                mt: '1.5rem',
                mb: '2rem',
                width: '20rem',
                height: '2rem',


            }}>
                <AddButton />
            </Box>



            {address && address.map((address) =>
                <Box sx={{
                    border: 1,
                    borderColor: 'lightgrey',
                    width: '60rem',
                    height: '5rem',
                    display: 'flex',
                    justifyContent: 'space-between',
                    alignItems: 'center',
                    pl: '2rem',
                    pr: '2rem'
                }}
                    key={address._id}>
                    <Box>
                        <Box sx={{
                            display: 'flex',
                            gap: 3
                        }}>
                            <Typography>{address.name}</Typography>
                            <Typography>{address.number}</Typography>
                        </Box>
                        <Box>
                            <Typography>{address.address + ',' + address.city}<b>{address.pin}</b></Typography>
                        </Box>
                    </Box>
                    <Box sx={{
                        pt: '2rem',
                        display: 'flex',
                        gap: 2
                    }}>
                        <IconButton sx={{ color: 'green' }} aria-label="add to shopping cart">
                            <EditIcon />
                        </IconButton>
                        <IconButton sx={{ color: 'red' }} aria-label="add to shopping cart">
                            <DeleteIcon />
                        </IconButton>
                    </Box>
                </Box>
            )}



        </Box>

    )
}

export default Address
