import { Paper, Typography } from '@mui/material'
import { Box } from '@mui/system'
import React from 'react'
import './Address.css'
import Changebtn from './ChangeAddress/ChangeAddressbtn'

function Address({ address, name, number }) {
    const [address1, setAddress1] = React.useState('')
    const [address2, setAddress2] = React.useState('')
    const [pin, setPin] = React.useState('')

    React.useEffect(() => {
        setAddress1(address[0].address)
        setAddress2(address[0].city)
        setPin(address[0].pin)
    }, [])


    return (
        <Paper sx={{
            display: 'flex',
            justifyContent: 'space-between',
            m: '1rem',
        }}>


            <Box sx={{
                width: '60rem',
                height: '5rem',
                pl: '2rem',
                pr: '2rem',
                pt: '1rem'

            }}
                key={address[0]._id}>
                <Box>
                    <Box sx={{
                        display: 'flex',
                        gap: 3
                    }}>
                        <Typography><b>Delivery to:</b></Typography>
                        <Typography>{name}</Typography>
                        <Typography>{number}</Typography>
                    </Box>
                    <Box>
                        <Typography>{address1 + ',' + address2 + ' '}<b>{'pin:' + pin}</b></Typography>
                    </Box>
                </Box>

            </Box>
            <Changebtn />

        </Paper>
    )
}

export default Address
