        //         setCart(res.data)
        //         let { items, ...others } = res.data
        //         setCartitems(items)
        //         setItems(items)
        //     })
    }, [])


    return (
        <Box sx={{
            display: 'flex',
            bgcolor: '#F8F8F8'
        }}>
            <Box sx={{
                mt: '5.6rem',
                width: '60%'
            }}>
                <Address />

                {cart && <List items={items} />}

                <PlaceOrder />
            </Box>
            <Box sx={{
                width: '40%'
            }}>
                {cart && <TotalPrice cart={cart} items={cartitems} />}
            </Box>
        </Box>
    )
}

export default CartItems
