import * as React from 'react';
import Popover from '@mui/material/Popover';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';

export default function BasicPopover() {
    const [open, setopen] = React.useState(false)
    const [address, setAddress] = React.useState([])
    const [name, setName] = React.useState('')
    const [number, setNumber] = React.useState('')

    React.useEffect(() => {
        let user = localStorage.getItem("user")
        user = JSON.parse(user)
        const customConfig = {
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${user.token}`
            }
        }
        axios.get(`${baseUrl}/api/user/address`, customConfig)
            .then((res) => {
                setAddress(res.data.address)
                setName(res.data.firstName)
                setNumber(res.data.number)
                console.log(res.data)
            })
    }, [])

    const handleClick = () => {
        setopen(true)
    }

    const handleClose = () => {
        setopen(false)
    };


    const id = open ? 'simple-popover' : undefined;

    return (
        <div>
            <Button aria-describedby={id} variant="outlined" onClick={handleClick}
                sx={{
                    width: '5.5rem',
                    height: '2rem',
                    m: '2rem',
                }}>
                change
            </Button>
            <Popover
                id={id}
                open={open}
                onClose={handleClose}
                anchorOrigin={{
                    vertical: 'top',
                    horizontal: 'center',
                }}
                transformOrigin={{
                    vertical: 'top',
                    horizontal: 'center',
                }}
            >
                <Box sx={{
                    height: 300,
                    overflow: "hidden",
                    overflowY: "scroll",
                }}>
                    {address && address.map((address) =>
                        <Box sx={{
                            border: 1,
                            borderColor: 'lightgrey',
                            width: '60rem',
                            height: '5rem',
                            display: 'flex',
                            justifyContent: 'space-between',
                            alignItems: 'center',
                            pl: '2rem',
                            pr: '2rem'
                        }}
                            key={address._id}>
                            <Box>
                                <Box sx={{
                                    display: 'flex',
                                    gap: 3
                                }}>
                                    <Typography>{name}</Typography>
                                    <Typography>{number}</Typography>
                                </Box>
                                <Box>
                                    <Typography>{address.address + ',' + address.city + ' '}<b>{'pin:' + address.pin}</b></Typography>
                                </Box>
                            </Box>
                            <Box sx={{
                                pt: '2rem',
                                display: 'flex',
                                gap: 2
                            }}>
                                <IconButton onClick={() => handleDelete(address._id)} sx={{ color: 'red' }} aria-label="add to shopping cart">
                                    <DeleteIcon />
                                </IconButton>
                            </Box>
                        </Box>
                    )}
                </Box>
            </Popover>
        </div>
    );
}
