import * as React from 'react';
import PropTypes from 'prop-types';
import Tabs from '@mui/material/Tabs';
import Tab from '@mui/material/Tab';
import Typography from '@mui/material/Typography';
import Box from '@mui/material/Box';
import { Stack } from '@mui/system';
import { Grid } from '@mui/material';

function TabPanel(props) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`simple-tabpanel-${index}`}
      aria-labelledby={`simple-tab-${index}`}
      {...other}
    >
      {value === index && (
        <Box sx={{ p: 3 }}>
          <Typography>{children}</Typography>
        </Box>
      )}
    </div>
  );
}

TabPanel.propTypes = {
  children: PropTypes.node,
  index: PropTypes.number.isRequired,
  value: PropTypes.number.isRequired,
};

function a11yProps(index) {
  return {
    id: `simple-tab-${index}`,
    'aria-controls': `simple-tabpanel-${index}`,
  };
}

export default function BasicTabs() {
  const [value, setValue] = React.useState(0);

  const nonVeg = [
    { name: 'kera pickle', discription: 'sea fish' },
    { name: 'kakka pickle', discription: 'fresh water fish' },
    { name: 'pallathy', discription: 'fresh water fish' },
    { name: 'vayamp', discription: 'fresh water fish' },
    { name: 'kera pickle', discription: 'sea fish' },
    { name: 'kakka pickle', discription: 'fresh water fish' },
    { name: 'pallathy', discription: 'fresh water fish' },
    { name: 'vayamp', discription: 'fresh water fish' },
  ]

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  return (
    <Box sx={{
      width: '100%',
      mt: '2rem'
    }}>
      <Box sx={{
        borderBottom: 1,
        borderTop: 1,
        borderColor: 'divider',
        display: 'inlineFlex',
        justifyContent: 'center',
        alignItems: 'center'

      }}>
        <Tabs value={value} onChange={handleChange} aria-label="basic tabs example">
          <Tab label="NON-VEG" {...a11yProps(0)} />
          <Tab label="VEG" {...a11yProps(1)} />
        </Tabs>
      </Box>
      <TabPanel value={value} index={0} >
        <Grid container spacing={2}>
          {nonVeg.map((item) =>
          <Grid item xs={2}>
            <Box sx={{
              bgcolor: 'lightgrey',
              width: '15rem',
              height: '16rem',
              position: 'relative',
              gap:1
            }}>
              <Box sx={{
                width: '15rem',
                height: '9rem',
                bgcolor: 'red',
                position: 'absolute'
              }}>
                imag
              </Box>
              <Box sx={{
                display: 'flex',
                flexDirection: 'column',
                justifyContent: 'center',
                alignItems: 'center',
                position: 'relative',
                pt: '9rem'
              }}>
                <Typography>name</Typography>
                <Typography>discription</Typography>
              </Box>
              <Box sx={{
                display: 'flex',
                flexDirection: 'row',
                justifyContent: 'space-around',
                alignItems: 'center',
                mt: '2rem',
                position: 'relative'
              }}>
                <Typography>watchlist</Typography>
                <Typography>add to cart</Typography>
              </Box>
            </Box>
            </Grid>
          )}
        </Grid>
      </TabPanel>
      <TabPanel value={value} index={1}>
        veg
      </TabPanel>
    </Box>
  );
}
