        //     amount: cartitems.bill + 40,
        //     address: address._id,
        //     items: cartitems.items
        // }

        // if (cartitems.items) {
        //     axios.post(`${baseUrl}/api/order/placeOrder`, Data, customConfig)
        //         .then((res) => {
        //             navigate('/order')
        //         })
        // } else {
        //     alert('cart is empty')
        // }
    }


    return (
        <Box sx={{
            display: 'flex',
            bgcolor: '#F8F8F8',
            pb: '9rem'
        }}>
            <Box sx={{
                mt: '5.6rem',
                width: '60%'
            }}>
                <Address address={address} name={name} number={number} />

                <List items={cartitems.items} />

                <Box>
                    <Paper sx={{
                        position: 'relative',
                        display: 'flex',
                        justifyContent: 'end',
                        m: '1rem'
                    }}>
                        <Button variant="contained" sx={{
                            bgcolor: '#ef6c00',
                            "&:hover": { backgroundColor: "#ef6c00" },
                            m: '1.5rem'
                        }}
                            onClick={handlePlaceOrder}
                        >Place Order</Button>
                    </Paper>
                </Box>
            </Box>
            <Box sx={{
                width: '40%'
            }}>
                {cart && <TotalPrice bill={cartitems.bill} items={cartitems.items} />}
            </Box>
        </Box>
    )
}

export default CartItems
