import { Grid, Paper, Typography } from '@mui/material'
import { Box } from '@mui/system'
import React, { useContext, useState } from 'react'
import { useNavigate } from 'react-router-dom'
import { UserContext } from '../../Context/Context'
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';
import './product.css'


function products({ products }) {
    const navigate = useNavigate()
    const { setDetails } = useContext(UserContext)
    const [icon, seticon] = useState(false)

    const handleView = (item) => () => {
        console.log(item);
        if(item) setDetails(item)
        navigate('/view')
    }

    const handleIcon=(index)=>()=>{
        console.log(index);
        seticon(!icon)
    }


    return (
        <Grid component={div} container spacing={2} sx={{ ml: '1rem' }}>
            {products.map((item,index) =>
                <Grid item xs={6} md={2} key={index}>
                    <Paper component={'div'} elevation={3} sx={{ ml: '-2rem', width: '100%', position: 'relative' }}>
                    {icon? <FavoriteBorderIcon key={index} onClick={handleIcon(index)} className='like_btn' style={{ color:"red"}} /> : <FavoriteBorderIcon onClick={handleIcon} key={index} className='like_btn' />}
                        <Box className='box' sx={{
                            width: '15rem',
                            height: '17rem',
                            position: 'relative',
                            mt: '2rem',
                            pt: '1rem',
                            pr: '3rem'
                        }} onClick={handleView(item)}>

                            <Box sx={{
                                width: '15rem',
                                height: '9rem',
                                position: 'absolute',
                                pl: '-0.1rem'
                            }}>

                                <img alt='' src={item.url} width='85%' height='100%'></img>
                            </Box>
                            <Box sx={{
                                display: 'flex',
                                flexDirection: 'column',
                                justifyContent: 'center',
                                alignItems: 'center',
                                position: 'relative',
                                pt: '9rem'
                            }}>
                                <Typography component={'span'}>{item.name}</Typography>
                                <Typography component={'span'}>{item.discription}</Typography>
                                <Typography component={'span'}>{item.weight}</Typography>
                                <Typography component={'span'}>{"₹"+item.rate+"    "+item.off+"off"}</Typography>
                            </Box>

                        </Box>
                    </Paper>
                </Grid>
            )}
        </Grid>
    )
}

export default products
