import * as React from 'react';
import PropTypes from 'prop-types';
import Tabs from '@mui/material/Tabs';
import Tab from '@mui/material/Tab';
import Box from '@mui/material/Box';

function TabPanel(props) {
    const { children, value, index, ...other } = props;

    return (
        <div
            role="tabpanel"
            hidden={value !== index}
            id={`vertical-tabpanel-${index}`}
            aria-labelledby={`vertical-tab-${index}`}
            {...other}
        >
            {value === index && (
                <Box sx={{ p: 3 }}>
                    <Box>{children}</Box>
                </Box>
            )}
        </div>
    );
}

TabPanel.propTypes = {
    children: PropTypes.node,
    index: PropTypes.number.isRequired,
    value: PropTypes.number.isRequired,
};

function a11yProps(index) {
    return {
        id: `vertical-tab-${index}`,
        'aria-controls': `vertical-tabpanel-${index}`,
    };
}

export default function VerticalTabs() {
    const [value, setValue] = React.useState(0);

    const handleChange = (event, newValue) => {
        setValue(newValue);
    };

    var styles = {
        default_tab:{
          color: Colors.grey500,
          backgroundColor: Colors.grey50,
          fontWeight: 400,
        },
        active_tab:{
          color: Colors.deepOrange700,
        }
      }

      styles.tab = []
      styles.tab[0] = styles.default_tab;
      styles.tab[1] = styles.default_tab;
      styles.tab[2] = styles.default_tab;
      styles.tab[3] = styles.default_tab;
      styles.tab[4] = styles.default_tab;
      styles.tab[5] = styles.default_tab;
      styles.tab[6] = styles.default_tab;
      styles.tab[7] = styles.default_tab;
      styles.tab[this.state.slideIndex] = objectAssign({},   styles.tab[this.state.slideIndex], styles.active_tab);

    return (
        <Box
            sx={{ flexGrow: 1, bgcolor: 'background.paper', display: 'flex', height: '32rem', pt: '7rem' }}
        >
            <Tabs
                orientation="vertical"
                value={value}
                onChange={handleChange}
                aria-label="Vertical tabs example"
                sx={{ 
                    borderRight: 1, 
                    borderColor: 'divider'
                }}
            >
                <Tab style={styles.tab[0]} label="Profile information" {...a11yProps(0)} />
                <Tab style={styles.tab[1]} label="Manage Address" {...a11yProps(1)} />
                <Tab style={styles.tab[2]} label="My Orders" {...a11yProps(2)} />
                <Tab style={styles.tab[3]} label="WishList" {...a11yProps(3)} />
                <Tab style={styles.tab[4]} label="Gift Cards" {...a11yProps(4)} />
                <Tab style={styles.tab[5]} label="Coupons" {...a11yProps(5)} />
                <Tab style={styles.tab[6]} label="Notification" {...a11yProps(6)} />
                <Tab style={styles.tab[7]} label="Payment Details" {...a11yProps(7)} />
            </Tabs>
            <TabPanel value={value} index={0}>
                Item One
            </TabPanel>
            <TabPanel value={value} index={1}>
                Item Two
            </TabPanel>
            <TabPanel value={value} index={2}>
                Item Three
            </TabPanel>
            <TabPanel value={value} index={3}>
                Item Four
            </TabPanel>
            <TabPanel value={value} index={4}>
                Item Five
            </TabPanel>
            <TabPanel value={value} index={5}>
                Item Six
            </TabPanel>
            <TabPanel value={value} index={6}>
                Item Seven
            </TabPanel>
            <TabPanel value={value} index={7}>
                Item Eight
            </TabPanel>
        </Box>
    );
}
