import { Box } from '@mui/material'
import React, { useContext, useEffect } from 'react'
import List from './List/List'
import Address from './Address/Address'
import TotalPrice from './TotalPrice/TotalPrice'
import PlaceOrder from './PlaceOrder/PlaceOrder'
import axios from 'axios'
import { baseUrl } from '../../url'
import { UserContext } from '../../Context/Context'

function CartItems() {
    const [cart, setCart] = React.useState(false)
    const { cartitems, setCartitems } = useContext(UserContext)
    const [address, setAddress] = React.useState([])
    const [name, setName] = React.useState('')
    const [number, setNumber] = React.useState('')

    useEffect(() => {
        let user = localStorage.getItem("user")
        user = JSON.parse(user)
        const customConfig = {
            headers: {
                'Authorization': `Bearer ${user.token}`
            }
        }

        axios.get(`${baseUrl}/api/cart/cartitems`, customConfig)
            .then((res) => {
                setCart(true)
                setCartitems(res.data)

            })


        axios.get(`${baseUrl}/api/user/address`, customConfig)
            .then((res) => {
                if (res.data) {
                    const lastaddress = res.data.address.pop()
                    setAddress(lastaddress)
                }
                setName(res.data.firstName)
                setNumber(res.data.number)
                console.log(res.data)
            })

    }, [])


    return (
        <Box sx={{
            display: 'flex',
            bgcolor: '#F8F8F8'
        }}>
            <Box sx={{
                mt: '5.6rem',
                width: '60%'
            }}>
                {address && <Address address={address} name={name} number={number} />}

                {cart && <List items={cartitems.items} />}

                <PlaceOrder />
            </Box>
            <Box sx={{
                width: '40%'
            }}>
                {cart && <TotalPrice bill={cartitems.bill} items={cartitems.items} />}
            </Box>
        </Box>
    )
}

export default CartItems
