import React, { useState } from "react";
import Button from '@mui/material/Button';
import { Box, Typography } from "@mui/material";

const UploadImage = () => {
    const [images, setImages] = useState();

    const upload = (event) => {
        setImages(event.target.files[0]);
        // const data = new FormData();
        // data.append('file', event.target.files[0]);
        // axios.post(`${baseUrl}/imageUpdate`, data, { withCredentials: true }).then((doc) => {

        // })
    }

    const uploadFile = () => {
        console.log('img uploaded')
    }

    return (
        <Box>
            <Typography>Upload Image</Typography>
            {images && (
                <div>
                    <img alt="not fount" width={"250px"} height={"250px"} src={URL.createObjectURL(images)} />
                    <br />
                    <Button onClick={uploadFile} sx={{ mt: 1, mb: 1, pl: 2 }} variant="contained">Upload</Button>
                </div>
            )}
            <Box sx={{
                bottom:"0px"
            }}>
                <input
                    type="file"
                    name="file"
                    onChange={upload}
                />
            </Box>
        </Box>
    );
};

export default UploadImage;
