import * as React from 'react';
import Logo from './logo/logo';
import Cart from './cartIcon/cartIcon';
import Account from './accountIcon/accountIcon';
import SearchBar from './SearchBar/SearchBar'
import './header.css';
import Box from '@mui/material/Box';
import IconButton from '@mui/material/IconButton';
import { Typography } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import Adminicon from './adminicon/AdminIcon';
import { UserContext } from '../../Context/Context';


export default function PrimarySearchAppBar() {
    const { user } = React.useContext(UserContext)
    const [name, setName] = React.useState({})
    const [admin, setAdmin] = React.useState(false)
    const [token, setToken] = React.useState()
    const navigate = useNavigate()
    React.useEffect(() => {
        let token = localStorage.getItem('token')
        setToken(JSON.parse(token))
        if (user) {
            setName(user)
            setAdmin(user.isAdmin)
        }
    }, [])



    return (
        <Box className='header' sx={{ borderBottom: 1 }}>
            <Box sx={{
                position: 'relative',
                width: '12rem',
                height: '4rem',
                display: { xs: 'none', sm: 'block' }
            }}>
                <Logo />
            </Box>
            <Box>
                <SearchBar />
            </Box>
            <Box >
                {token ? (
                    <Box className='right-items'>
                        {admin ? (
                            <Box>
                                <IconButton>
                                    <Adminicon />
                                </IconButton>
                            </Box>
                        ) : (
                            <Box>
                                <IconButton>
                                    <Cart />
                                </IconButton>
                                <IconButton>
                                    <Account />
                                </IconButton>
                            </Box>
                        )}
                    </Box>

                ) : (
                    <Box className='right-items'>
                        <Typography className='selection' onClick={() => navigate('/login')}>Login</Typography>
                        <Typography>/</Typography>
                        <Typography className='selection' onClick={() => navigate('/signup')}>Signup</Typography>
                    </Box>
                )
                }



            </Box >
        </Box >
    );
}
