import { Box, Typography } from '@mui/material'
import React, { useEffect, useState } from 'react'

function view({details}) {
    // const [product,setProduct]=useState({})
    let product
    useEffect(() => {
        console.log('pro: '+details);
      if(details) localStorage.setItem('detail',details)
      product =localStorage.getItem('detail')
    //   setProduct(prod)

      console.log(product);
    //   console.log(product);

    }, [])


    return (
        <Box sx={{
            position: 'relative',
            width: '100%',
            height: '30rem',
            display: 'flex',
            top: '6rem',
            bgcolor: 'red',
            mb: '1rem'
        }}>
            <Box>
                <img alt='' src={product?  product.url : ''}></img>
            </Box>
            <Box>
                <Typography>Product Details</Typography>
                <Box>
                    <Typography>Rate:</Typography>
                    <Typography>name:{product? product.name : ''}</Typography>
                    <Typography>contact:</Typography>
                    <Typography>category:</Typography>
                    <Typography>type:</Typography>
                    <Typography>date:</Typography>
                </Box>
            </Box>
        </Box>
    )
}

export default view
