export default function BasicTabs() {
  const [value, setValue] = React.useState(0);

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  return (
    <Box sx={{
      width: '100%',
      mt: '2rem'
    }}>
      <Box sx={{
        borderBottom: 1,
        borderTop: 1,
        borderColor: 'divider',
        display: 'inlineFlex',
        justifyContent: 'center',
        alignItems: 'center'

      }}>
        <Tabs value={value} onChange={handleChange} aria-label="basic tabs example">
          <Tab label="NON-VEG" {...a11yProps(0)} />
          <Tab label="VEG" {...a11yProps(1)} />
        </Tabs>
      </Box>
      <TabPanel value={value} index={0}>
        <Box sx={{
          bgcolor: 'lightgrey',
          width: '15rem',
          height: '16rem',
          position:'relative'
        }}>
          <Box sx={{
            width: '15rem',
            height: '9rem',
            bgcolor: 'red',
            position:'absolute'
          }}>
            imag
          </Box>
          <Box sx={{
            display:'flex',
            flexDirection:'column',
            justifyContent:'center',
            alignItems:'center',
            position:'relative',
            pt:'9rem'
          }}>
            <Typography>name</Typography>
            <Typography>discription</Typography>
          </Box>
          <Box sx={{
            display: 'flex',
            flexDirection: 'row',
            justifyContent: 'space-around',
            alignItems: 'center',
            mt:'2rem',
            position:'relative'
          }}>
            <Typography>watchlist</Typography>
            <Typography>add to cart</Typography>
          </Box>
        </Box>
      </TabPanel>
      <TabPanel value={value} index={1}>
        veg
      </TabPanel>
    </Box>
  );
}
