import { Box, Button, MenuItem, Select, TextField, Typography } from '@mui/material'
import React, { useState } from 'react'
import SearchIcon from '@mui/icons-material/Search';
// import IconButton from "@mui/material/IconButton";
import styled from '@emotion/styled';
import InputAdornment from '@mui/material/InputAdornment';
import KeyboardArrowDownIcon from '@mui/icons-material/KeyboardArrowDown';
import { ClickAwayListener } from '@mui/base';
import Menu from '@mui/material/Menu';



const CssTextField = styled(TextField)({
  '& label.Mui-focused': {
    color: 'green',
  },
  '& .MuiInput-underline:after': {
    borderBottomColor: 'green',
  },
  '& .MuiOutlinedInput-root': {
    '& fieldset': {
      borderColor: 'white',
    },
    '&:hover fieldset': {
      borderColor: 'white',
    },
    '&.Mui-focused fieldset': {
      borderColor: 'white',
    },
  },
  '&::placeholder': {
    fontStyle: 'italic',
    color: 'green'
  },
  "& .MuiInputBase-root": {
    color: 'grey'
  }
});


function SearchBar() {

  const [searchInput, setSearchInput] = useState("");
  const [list, setList] = useState([])
  const [open, setOpen] = useState(false)
  const [option, setOption] = useState(0)

  const countries = [

    { name: "Belgium", continent: "Europe" },
    { name: "India", continent: "Asia" },
    { name: "Bolivia", continent: "South America" },
    { name: "Ghana", continent: "Africa" },
    { name: "Japan", continent: "Asia" },
    { name: "Canada", continent: "North America" },
    { name: "New Zealand", continent: "Australasia" },
    { name: "Italy", continent: "Europe" },
    { name: "South Africa", continent: "Africa" },
    { name: "China", continent: "Asia" },
    { name: "Paraguay", continent: "South America" },
    { name: "Usa", continent: "North America" },
    { name: "France", continent: "Europe" },
    { name: "Botswana", continent: "Africa" },
    { name: "Spain", continent: "Europe" },
    { name: "Senegal", continent: "Africa" },
    { name: "Brazil", continent: "South America" },
    { name: "Denmark", continent: "Europe" },
    { name: "Mexico", continent: "South America" },
    { name: "Australia", continent: "Australasia" },
    { name: "Tanzania", continent: "Africa" },
    { name: "Bangladesh", continent: "Asia" },
    { name: "Portugal", continent: "Europe" },
    { name: "Pakistan", continent: "Asia" },

  ];

  const handleChange = (e) => {
    e.preventDefault();
    setSearchInput(e.target.value);
    const text = e.target.value
    const index = text.length
    setList([...countries.filter(e => e.name.slice(0, index) === text)])
    console.log(countries.filter(e => e.name.slice(0, index) === text));
    if (list.length > 0) {
      setOpen(true)
    }
    if (list.length === 0) setOpen(false)
    // console.log(countries.filter((item) => item.name.toLocaleLowerCase() === searchInput.toLocaleLowerCase()));
  };

  const handleSelect = (e) => {
    setOption(e.target.value)
  }


  return (
    <Box sx={{
      width: '100%',
      height: '3rem',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      gap: 1,
      pt: 0.4
    }}>
      <Box sx={{
        borderRight: 1,
        borderColor: 'grey.500'
      }}>
        <Select sx={{
          width: '20%'
        }}
          // value={option}
          onChange={handleSelect}
        >
          <MenuItem value={1}>Red</MenuItem>
          <MenuItem value={2}>Black</MenuItem>
          <MenuItem value={3}>Blue</MenuItem>
          <MenuItem value={4}>Green</MenuItem>
          <MenuItem value={5}>Yellow</MenuItem>
        </Select>
        {/* <Button
          variant="text"
          sx={{
            bgcolor: 'white',
            color: 'black',
            ml: 1,
            mr: 1,
            '&:hover': {
              backgroundColor: 'white',
            }
          }} endIcon={< KeyboardArrowDownIcon />}>
          All Collections
        </Button> */}
      </Box>
      <Box>
        <form>
          <CssTextField
            // color='secondary'
            id="search-bar"
            className="text"
            variant="outlined"
            InputProps={{
              startAdornment: (
                <InputAdornment position="start">
                  <SearchIcon sx={{ color: 'grey' }} />
                </InputAdornment>
              )
            }}
            placeholder="Search our product..."
            size="small"
            onChange={handleChange}
            value={searchInput}
          />
          <Button
            variant="contained"
            sx={{
              bgcolor: 'black',
              ml: 1,
              mr: 1,
              '&:hover': {
                backgroundColor: 'black',
              }
            }} startIcon={< SearchIcon />}>
            search
          </Button>

        </form>
      </Box>
      {/* < Menu
        // sx={{boxShadow:1}}
        id="menu-appbar"
        anchorOrigin={{
          vertical: 100,
          horizontal: 500,
        }}
        keepMounted
        transformOrigin={{
          vertical: 'top',
          horizontal: 'left',
        }}
        open={open}
        onClose={e => setOpen(false)}
      >
        <ClickAwayListener onClickAway={() => setOpen(false)}>
          {list && list.length > 0 && list.map((item, i) =>
            <Box sx={{ bgcolor: 'white' }}>
              <Typography
                key={i}
                onClick={() => {
                  setOpen(!open)
                  setSearchInput(item.name)
                  setList([])
                }}
              >{item.name}</Typography>
            </Box>
          )}
        </ClickAwayListener>

      </Menu> */}

      {/* <Box sx={{
        position: 'fixed',
        top: '4.2rem',
        pr: '3rem'
      }}>

      </Box> */}


    </Box>
  )
}
export default SearchBar
