import { Paper, Typography } from '@mui/material'
import { Box } from '@mui/system'
import React from 'react'
import Button from '@mui/material/Button';
import './Address.css'
import Changebtn from './ChangeAddress/ChangeAddressbtn'
import axios from 'axios';
import { baseUrl } from '../../../url'

function Address() {
    const [address, setAddress] = React.useState([])
    const [name, setName] = React.useState('')
    const [number, setNumber] = React.useState('')

    React.useEffect(() => {
        let user = localStorage.getItem("user")
        user = JSON.parse(user)
        const customConfig = {
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${user.token}`
            }
        }
        axios.get(`${baseUrl}/api/user/address`, customConfig)
            .then((res) => {
                if (res.data) {
                    const lastaddress = res.data.address.pop()
                    setAddress(lastaddress)
                }
                setName(res.data.firstName)
                setNumber(res.data.number)
                console.log(res.data)
            })
    }, [])
    return (
        <Paper sx={{
            display: 'flex',
            justifyContent: 'space-between',
            m: '1rem',
        }}>

            <Box sx={{
                width: '60rem',
                height: '5rem',
                pl: '2rem',
                pr: '2rem',

            }}
                key={address._id}>
                <Box>
                    <Box sx={{
                        display: 'flex',
                        gap: 3
                    }}>
                        <Typography>{name}</Typography>
                        <Typography>{number}</Typography>
                    </Box>
                    <Box>
                        <Typography>{address.address + ',' + address.city + ' '}<b>{'pin:' + address.pin}</b></Typography>
                    </Box>
                </Box>

            </Box>

            <Changebtn />

        </Paper>
    )
}

export default Address
